void draw_bird(int x, int y, double scale, COLORREF color, int pupil_x, int pupil_y, int dir, int left_paw, int right_paw, int beak_close)                                            // pupil_x- положение зрачка по оси x, pupil_y- положение зрачка по оси y. ,   left_paw ,right_pawб левая и правая лапа соответственно (1 или-1)            Сумма этих параметров НЕ ДОЛЖНА быть больше 27, и меньше 27        dir это разворот      1 или -1
{
     txSetColor (TX_BLACK,int(5*scale));
     txSetFillColor (TX_WHITE);
     POINT bird [26] =  {                               // тело
                        {x+int(50*scale*dir ),  y+int(100*scale) },
                        {x+int(75*scale*dir ),  y+int(50*scale) },
                        {x+int(100*scale*dir),  y+int(25*scale) },
                        {x+int(150*scale*dir),  y               },
                        {x+int(200*scale*dir),  y               },
                        {x+int(250*scale*dir),  y+int(25*scale) },
                        {x+int(275*scale*dir),  y+int(50*scale) },
                        {x+int(300*scale*dir),  y+int(100*scale)},
                        {x+int(325*scale*dir),  y+int(125*scale)},
                        {x+int(375*scale*dir),  y+int(150*scale)},
                        {x+int(500*scale*dir),  y+int(150*scale)},
                        {x+int(450*scale*dir),  y+int(200*scale)},
                        {x+int(350*scale*dir),  y+int(200*scale)},
                        {x+int(425*scale*dir),  y+int(200*scale)},
                        {x+int(375*scale*dir),  y+int(250*scale)},
                        {x+int(275*scale*dir),  y+int(250*scale)},
                        {x+int(325*scale*dir),  y+int(250*scale)},
                        {x+int(275*scale*dir),  y+int(300*scale)},
                        {x+int(200*scale*dir),  y+int(300*scale)},
                        {x+int(200*scale*dir),  y+int(300*scale)},
                        {x+int(175*scale*dir),  y+int(300*scale)},
                        {x+int(125*scale*dir),  y+int(275*scale)},
                        {x+int(100*scale*dir),  y+int(250*scale)},
                        {x+int(75*scale*dir ),  y+int(200*scale)},
                        {x+int(75*scale*dir ),  y+int(150*scale)},
                        {x+int(50*scale*dir ),  y+int(100*scale)}};
     txPolygon (bird, 26);
     txSetFillColor (color);
     txFloodFill (x+int(100*scale*dir), y+int(100*scale)                 );
     txSetFillColor (TX_WHITE);
     txCircle    (x+int(175*scale*dir), y+int(100*scale), int(37.5*scale));
     txSetFillColor (TX_BLACK);
     txCircle    (x+int((175+pupil_x)*scale*dir), y+int((100+pupil_y)*scale), int(12.5*scale));
     if (left_paw==1)
     {
     txLine(x+int(175*scale*dir), y+int(300*scale),    x+int(175*scale*dir), y+int(375*scale));
     txLine(x+int(150*scale*dir), y+int(375*scale),    x+int(175*scale*dir), y+int(350*scale));
     txLine(x+int(175*scale*dir), y+int(350*scale),    x+int(200*scale*dir), y+int(375*scale));
     }

     if (right_paw==1)
     {
     txLine(x+int(250*scale*dir), y+int(300*scale),    x+int(250*scale*dir), y+int(375*scale));
     txLine(x+int(225*scale*dir), y+int(375*scale),    x+int(250*scale*dir), y+int(350*scale));
     txLine(x+int(250*scale*dir), y+int(350*scale),    x+int(275*scale*dir), y+int(375*scale));
     }

     if (beak_close==1)
     {
     txLine(x+int(50*scale*dir),y+int(100*scale),x,y+int(150*scale));
     txLine(x+int(75*scale*dir),y+int(150*scale),x,y+int(150*scale));
     txLine(x+int(25*scale*dir),y+int(150*scale),x+int(75*scale*dir),y+int(200*scale));
     }
     else
     {
     txLine(x+int(50*scale*dir),y+int(100*scale),x,y+int(125*scale));
     txLine(x,y+int(125*scale),x+int(75*scale*dir),y+int(150*scale));
     txLine(x+int(75*scale*dir),y+int(150*scale),x+int(25*scale*dir),y+int(175*scale));
     txLine(x+int(25*scale*dir),y+int(175*scale),x+int(75*scale*dir),y+int(200*scale));
     }

     txSetFillColor (TX_YELLOW);
     txFloodFill (x+int(65*scale*dir), y+int(140*scale));
     txFloodFill (x+int(65*scale*dir), y+int(160*scale));
}

void draw_flower(int x, int y, double scale, int dir)
{
int xx = x;
int yy = y;
txSetColor(RGB(29, 82, 20),int(5*scale));
txLine(xx,yy,xx,yy-125);
if (dir==1)
draw_leave(xx+50,yy-100,0.5,-1);
else
{
draw_leave(xx-50,yy-100,0.5,1);
}
txSetColor(RGB(189, 39, 9),5);
txSetFillColor(RGB(252, 94, 3));
txCircle(xx+25,yy-175,25);
txCircle(xx+25,yy-125,25);
txCircle(xx-25,yy-175,25);
txCircle(xx-25,yy-125,25);
txSetColor(RGB(252, 177, 3),5);
txSetFillColor(RGB(252, 219, 3));
txCircle(xx,yy-150,25);
}

void draw_leave(int x, int y, double scale, int dir)
{
    txSetColor(RGB(29, 82, 20),int(5*scale));
    if (dir==1)
    {
        txArc(x,                    y-int(100*scale), x+int(200*scale*dir), y+int(100*scale),180,90);
        txArc(x-int(100*scale*dir), y,                x+int(100*scale*dir), y+int(200*scale),0,  90);
    }
    else
    {
        txArc(x,                    y-int(100*scale), x+int(200*scale*dir), y+int(100*scale),270,90);
        txArc(x-int(100*scale*dir), y,                x+int(100*scale*dir), y+int(200*scale),90,  90);
    }
    txSetFillColor(RGB(137, 237, 130));
    txFloodFill(x+int(15*scale*dir),  y+int(10*scale));

    txSetColor(RGB(70, 176, 51),int(5*scale));
    txLine(x+int(25*scale*dir),y+int(25*scale),x+int(25*scale*dir),y+int(5*scale));
    txLine(x+int(25*scale*dir),y+int(25*scale),x+int(5*scale*dir),y+int(25*scale));
    txLine(x+int(50*scale*dir),y+int(50*scale),x+int(15*scale*dir),y+int(50*scale));
    txLine(x+int(50*scale*dir),y+int(50*scale),x+int(50*scale*dir),y+int(15*scale));
    txLine(x+int(75*scale*dir),y+int(75*scale),x+int(37*scale*dir),y+int(75*scale));
    txLine(x+int(75*scale*dir),y+int(75*scale),x+int(75*scale*dir),y+int(37*scale));

    txSetColor(RGB(75, 150, 60),int(5*scale));
    txLine(x,y,x+int(100*scale*dir),y+int(100*scale));


}
void draw_sun(int x, int y, double scale, COLORREF color, int start_ray, int end_ray, int dir, int face)                                   // start_ray- начало луча    start_ray>100       end_ray- конец луча   end_ray>500         если face=1, то лица нет . если не =1
{
     txSetFillColor (color);
     txSetColor (TX_BLACK,int(5*scale));        // солнце
     txCircle  (x,y,int(100*scale));
     txSetFillColor (TX_BLACK);
     if (face==1)
     {
     txCircle  (x,y,int(5*scale*dir));
     txArc     (x-int(50*scale),  y-int(50*scale*dir), x+int(50*scale),   y+int(50*scale*dir), 180, 180);
     txCircle  (x-int(40*scale),  y-int(40*scale*dir), int(5*scale));
     txCircle  (x+int(40*scale),  y-int(40*scale*dir), int(5*scale));
     }
     txSetColor (color,int(5*scale));
     txLine    (x+int((150+start_ray)*scale), y,                x+int((250+end_ray)*scale), y               );
     txLine    (x-int((150+start_ray)*scale), y,                x-int((250+end_ray)*scale), y               );
     txLine    (x,                y+int((150+start_ray)*scale*dir), x,                y+int((250+end_ray)*dir*scale));
     txLine    (x,                y-int((150+start_ray)*scale*dir), x,                y-int((250+end_ray)*dir*scale));

     txLine    (x+int((100+start_ray)*scale), y-int((100+start_ray)*scale*dir), x+int((200+end_ray)*scale), y-int((200+end_ray)*scale*dir));
     txLine    (x-int((100+start_ray)*scale), y-int((100+start_ray)*scale*dir), x-int((200+end_ray)*scale), y-int((200+end_ray)*scale*dir));
     txLine    (x+int((100+start_ray)*scale), y+int((100+start_ray)*scale*dir), x+int((200+end_ray)*scale), y+int((200+end_ray)*scale*dir));
     txLine    (x-int((100+start_ray)*scale), y+int((100+start_ray)*scale*dir), x-int((200+end_ray)*scale), y+int((200+end_ray)*scale*dir));
}

void draw_cat(int x, int y, double scale, COLORREF color, int left_eye, int right_eye, int dir, int collar)                                           //котег     левый глаз полосочка при left_eye=50     правый глаз полосочка right_eye=50    глаза галочки при left_eye=0 right_eye=0 соответственно   если collar= 1, то программа рисует ошейник, если не = 1, то не рисует
{
     txSetColor (TX_BLACK,int(5*scale));
     txSetFillColor (color);
     POINT cat [35] = {
                     {x+int(200*scale*dir ), y                }, {x+int(300*scale*dir),  y+int(100*scale) },
                     {x+int(350*scale*dir ), y+int(100*scale) }, {x+int(450*scale*dir ), y                },
                     {x+int(450*scale*dir ), y+int(100*scale) }, {x+int(500*scale*dir ), y+int(150*scale) },
                     {x+int(500*scale*dir ), y+int(300*scale) }, {x+int(450*scale*dir ), y+int(350*scale) },
                     {x+int(450*scale*dir ), y+int(400*scale) }, {x+int(500*scale*dir ), y+int(450*scale) },
                     {x+int(600*scale*dir ), y+int(450*scale) }, {x+int(750*scale*dir ), y+int(600*scale) },
                     {x+int(750*scale*dir ), y+int(1000*scale)}, {x+int(850*scale*dir ), y+int(1000*scale)},
                     {x+int(900*scale*dir ), y+int(950*scale) }, {x+int(900*scale*dir ), y+int(500*scale) },
                     {x+int(1000*scale*dir), y+int(600*scale) }, {x+int(1000*scale*dir), y+int(1000*scale)},
                     {x+int(900*scale*dir ), y+int(1100*scale)}, {x+int(450*scale*dir ), y+int(1100*scale)},
                     {x+int(450*scale*dir ), y+int(700*scale) }, {x+int(450*scale*dir ), y+int(1000*scale)},
                     {x+int(600*scale*dir ), y+int(1000*scale)}, {x+int(450*scale*dir ), y+int(1000*scale)},
                     {x+int(450*scale*dir ), y+int(1100*scale)}, {x+int(350*scale*dir ), y+int(1100*scale)},
                     {x+int(350*scale*dir ), y+int(700*scale) }, {x+int(300*scale*dir ), y+int(700*scale) },
                     {x+int(300*scale*dir ), y+int(1100*scale)}, {x+int(200*scale*dir ), y+int(1100*scale)},
                     {x+int(200*scale*dir ), y+int(350*scale) }, {x+int(150*scale*dir ), y+int(300*scale) },
                     {x+int(150*scale*dir ), y+int(150*scale) }, {x+int(200*scale*dir ), y+int(100*scale) },
                     {x+int(200*scale*dir ), y}};
     txPolygon (cat, 35);
     txFloodFill (x+int(600*scale*dir), y+int(600*scale));
     txLine(x+int(250*scale*dir),       y+int((150+left_eye)*scale),  x+int(300*scale*dir), y+int(200*scale));
     txLine(x+int(250*scale*dir),       y+int(200*scale),             x+int(300*scale*dir), y+int(200*scale));
     txLine(x+int(400*scale*dir),       y+int((150+right_eye)*scale), x+int(350*scale*dir), y+int(200*scale));
     txLine(x+int(350*scale*dir),       y+int(200*scale),             x+int(400*scale*dir), y+int(200*scale));
     txLine(x+int(250*scale*dir),       y+int(250*scale),             x+int(300*scale*dir), y+int(300*scale));
     txSetFillColor(TX_BLACK);
     txLine(x+int(300*scale*dir),       y+int(300*scale),             x+int(325*scale*dir), y+int(250*scale));
     txLine(x+int(325*scale*dir),       y+int(250*scale),             x+int(350*scale*dir), y+int(225*scale));
     txLine(x+int(350*scale*dir),       y+int(225*scale),             x+int(300*scale*dir), y+int(225*scale));
     txLine(x+int(300*scale*dir),       y+int(225*scale),             x+int(325*scale*dir), y+int(250*scale));
     txFloodFill(x+int(325*scale*dir),  y+int(235*scale));
     txLine(x+int(325*scale*dir),       y+int(250*scale),             x+int(350*scale*dir), y+int(300*scale));
     txLine(x+int(350*scale*dir),       y+int(300*scale),             x+int(400*scale*dir), y+int(250*scale));
     txLine(x,                          y+int(200*scale),             x+int(100*scale*dir), y+int(200*scale));
     txLine(x+int(100*scale*dir),       y+int(200*scale),             x+int(200*scale*dir), y+int(250*scale));
     txLine(x,                          y+int(250*scale),             x+int(100*scale*dir), y+int(250*scale));
     txLine(x+int(100*scale*dir),       y+int(250*scale),             x+int(200*scale*dir), y+int(300*scale));
     txLine(x+int(650*scale*dir),       y+int(200*scale),             x+int(550*scale*dir), y+int(200*scale));
     txLine(x+int(550*scale*dir),       y+int(200*scale),             x+int(450*scale*dir), y+int(250*scale));
     txLine(x+int(650*scale*dir),       y+int(250*scale),             x+int(550*scale*dir), y+int(250*scale));
     txLine(x+int(550*scale*dir),       y+int(250*scale),             x+int(450*scale*dir), y+int(300*scale));
     if(collar==1)
     {
     txLine(x+int(200*scale*dir),       y+int(350*scale),             x+int(325*scale*dir), y+int(400*scale));
     txLine(x+int(450*scale*dir),       y+int(350*scale),             x+int(325*scale*dir), y+int(400*scale));
     txLine(x+int(200*scale*dir),       y+int(400*scale),             x+int(325*scale*dir), y+int(450*scale));
     txLine(x+int(450*scale*dir),       y+int(400*scale),             x+int(325*scale*dir), y+int(450*scale));
     txSetFillColor (TX_RED);
     txFloodFill(x+int(250*scale*dir),  y+int(400*scale));
     txCircle(x+int(325*scale*dir),     y+int(500*scale), int(50*scale));
     txSetFillColor (TX_YELLOW);
     txFloodFill(x+int(325*scale*dir),  y+int(500*scale));
     }
}

void draw_bug(int x, int y, double scale, COLORREF color1, COLORREF color2, int left_mustache, int right_mustache, int points)                                         // left_mustache- левый ус     right_mustache- правый ус    left_mustache>0      right_mustache>0       если points = 1, то точки рисуются, если не=1, то не рисуются
{

     txSetFillColor (TX_BLACK);        // это божья коровка
     txCircle (x,y,int(37.5*scale));
     txSetFillColor (color1);
     txEllipse (x-int(75*scale ), y,x+int(75*scale), y+int(200*scale*dir));
     txLine    (x-int(75*scale ), y+int(100*scale*dir ), x-int(125*scale), y+int(75*scale*dir) );
     txLine    (x+int(125*scale), y+int(75*scale*dir  ), x+int(150*scale), y+int(100*scale*dir));
     txLine    (x+int(75*scale ), y+int(100*scale*dir ), x+int(125*scale), y+int(75*scale*dir) );
     txLine    (x-int(125*scale), y+int(75*scale*dir  ), x-int(150*scale), y+int(100*scale*dir));
     txLine    (x-int(100*scale), y+int(25*scale*dir  ), x-int(64*scale ), y+int(50*scale*dir) );
     txLine    (x-int(100*scale), y+int(25*scale*dir  ), x-int(125*scale), y+int(50*scale*dir) );
     txLine    (x+int(100*scale), y+int(25*scale*dir  ), x+int(64*scale ), y+int(50*scale*dir) );
     txLine    (x+int(100*scale), y+int(25*scale*dir  ), x+int(125*scale), y+int(50*scale*dir) );
     txLine    (x-int(64*scale ), y+int(150*scale*dir ), x-int(115*scale), y+int(125*scale*dir));
     txLine    (x-int(115*scale), y+int(125*scale*dir ), x-int(140*scale), y+int(150*scale*dir));
     txLine    (x+int(64*scale ), y+int(150*scale*dir ), x+int(115*scale), y+int(125*scale*dir));
     txLine    (x+int(115*scale), y+int(125*scale*dir ), x+int(140*scale), y+int(150*scale*dir));
     txSetFillColor (color2);
     if (points==1)
     {
     txCircle  (x-int(25*scale ), y+int(50*scale*dir  ), int(12.5*scale));
     txCircle  (x+int(25*scale ), y+int(50*scale*dir  ), int(12.5*scale));
     txCircle  (x-int(40*scale ), y+int(100*scale*dir ), int(12.5*scale));
     txCircle  (x+int(40*scale ), y+int(100*scale*dir ), int(12.5*scale));
     txCircle  (x-int(25*scale ), y+int(150*scale*dir ), int(12.5*scale));
     txCircle  (x+int(25*scale ), y+int(150*scale*dir ), int(12.5*scale));
     }
     txArc     (x+int(20*scale ), y-int(60*scale*dir  ), x+int(right_mustache*scale), y+int(10*scale*dir),90,120);
     txArc     (x-int(left_mustache*scale ), y-int(60*scale*dir  ), x-int(20*scale), y+int(10*scale*dir),1,90);
}

void draw_stickman(int x, int y, double scale, COLORREF color1, COLORREF color2, int xr_arm, int yr_arm, int dir, int bald)                         // color1- цвет лица color2- цвет кепки     xr_arm- положение руки по оси x (x>-51, x<51),  yr_arm- положение руки по оси y (y>-51, y<51) сумма x+y не длжна быть больше 50   если bald = 1, то лысина есть. если не  =1, то рисуется кепка
{
     txSetColor (TX_BLACK,int(5*scale));
     txLine(x                  , y+int(50*scale ), x                  ,  y+int(250*scale));         // туловище
     txLine(x                  , y+int(50*scale ), x+int(50*scale*dir),  y+int(150*scale));
     txLine(x+int(50*scale*dir), y+int(150*scale), x+int(int(50+xr_arm)*scale*dir),  y+int(int(150+yr_arm)*scale));
     txLine(x                  , y+int(50*scale ), x-int(50*scale*dir),  y+int(150*scale));
     txLine(x-int(50*scale*dir), y+int(150*scale), x-int(50*scale*dir),  y+int(250*scale));
     txLine(x                  , y+int(250*scale), x+int(50*scale*dir),  y+int(350*scale));
     txLine(x+int(50*scale*dir), y+int(350*scale), x+int(25*scale*dir),  y+int(450*scale));
     txLine(x                  , y+int(250*scale), x-int(50*scale*dir),  y+int(350*scale));
     txLine(x-int(50*scale*dir), y+int(350*scale), x-int(25*scale*dir),  y+int(450*scale));
     if (bald==1)
     {
     txSetFillColor (color1);
     txCircle( x, y, int(50*scale));
     txFloodFill (x, y);
     }
     else
     {
     txSetFillColor (color1);
     txArc( x-int(50*scale*dir), y-int(50*scale ), x+int(50*scale*dir),  y+int(50*scale),150,240);   // голова
     txSetFillColor (color2);
     txLine(x-int(50*scale*dir), y-int(25*scale ), x+int(100*scale*dir), y-int(25*scale));
     txArc( x-int(50*scale*dir), y-int(75*scale ), x+int(50*scale*dir),  y+int(25*scale),0,180);     //кепка
     txFloodFill (x            , y-int(35*scale));
     }

}
